mapper.delete:

delete from tb_login
where user_id = #{userId,jdbcType=VARCHAR}
  and login_account = #{loginAccount,jdbcType=VARCHAR}

mapper.update:

update tb_login
set login_pwd = #{loginPwd,jdbcType=VARCHAR},
  login_token = #{loginToken,jdbcType=VARCHAR},
  last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP},
  statu = #{statu,jdbcType=CHAR},
  remark = #{remark,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=VARCHAR}
  and login_account = #{loginAccount,jdbcType=VARCHAR}

mapper.select:

select user_id, login_account, login_pwd, login_token, last_login_time, statu, remark
from tb_login
where user_id = #{userId,jdbcType=VARCHAR}
  and login_account = #{loginAccount,jdbcType=VARCHAR}
